sys_script_client.script:
function onLoad() {
   //The Script field is where the code is placed that gets the value(s) that the business rule put in the scratchpad.
	g_form.showFieldMsg('u_requested_for', g_scratchpad.email);
	//This will display a message under the field u_user, which is the Requested For field.  The message, in this case, is just the scratchpad data, which we reference using whatever name we set in the business rule:  g_scratchpad .email

}
