sys_script.script:
(function executeRule(current, previous /*null when async*/) 
 {
	var urg_value = current.urgency;
	var desc_box = "";
	if(urg_value=='3') //Expedited Change
		{
			desc_box = "Business Justification for the activity : "+current.u_business_justification+" 
Reason for Urgency : "+current.u_reason_urgency+"
Business and/or non-business Impact: "+current.u_business_impact+"
Requested BY(incident/requester name): "+current.getDisplayValue('u_requested_by')+"
Downtime Approved By: "+current.getDisplayValue('u_downtime_approved')+"
UAT Approved By: "+current.getDisplayValue('u_uat_approved')+"
Affected Country: "+current.getDisplayValue('u_country')+"
Involved IT component(s) and module(s):  "+current.u_involved_components+"
Impacted IT component(s) and module(s): "+current.u_impacted_components+"
";
		}

	else if(urg_value=='2') //Emergency Change
		{
			desc_box = "Business Justification for the activity: "+current.u_business_justification+"
Business and/or non-business Impact: "+current.u_business_impact+"
Involved IT component(s) and module(s): "+current.u_involved_components+" 
Impacted IT component(s) and module(s): "+current.u_impacted_components+"
Affected Country: "+current.getDisplayValue('u_country')+"

1. What is the criticality of this change for the business, for example: 
     a. Invoicing stop: 
     b. Legal requirements: 
     c. Month End Closure (MEC) and Year End Closure (YEC): 
     d. Commercial impact: 
     e. Other - Explain in detail the reason for Emergency: 

2. Related P1 or P2 Incident: "+current.getDisplayValue('u_related_inc')+"

3. Approved by MIM (Name): "+current.getDisplayValue('u_approved_by_mim')+"

4. Approvals - Business Approval (Name) / Service Owner approval: "+current.getDisplayValue('u_appovals')+"

Attach relevant email approvals to this Retrospective change.
";
		}

	else //Normal Change
		{
			desc_box = "Business Justification for the activity: "+current.u_business_justification+"
Business and/or non-business Impact: "+current.u_business_impact+"
Involved IT component(s) and module(s): "+current.u_involved_components+"
Impacted IT component(s) and module(s): "+current.u_impacted_components+"
Affected Country: "+current.getDisplayValue('u_country')+"
Requested By: "+current.getDisplayValue('u_requested_by')+"
Reason for Implementation in Business hours (if applicable): "+current.u_reason_business+"
Reason for longer window (if change window is more than 1 day): "+current.u_reason_window+"
";
		}

	current.description = desc_box;



})(current, previous);
