sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) 
{

	var db_view = new GlideRecord('x_58872_needit_u_latest_five_incident_consolidated_db_view');
	db_view.orderBy('inc_caller_id');
	db_view.query();
	var callers = [];
	while(db_view.next())
		{
			callers.push(db_view.getValue('user_name'));
		}	
	var temp ="";
	for(var u=0; u<callers.length; u++)
		{
			if(callers[u]===temp)
				{
					continue;
				}
			var rec = new GlideQuery('x_58872_needit_u_latest_five_incident_consolidated_db_view')
			.where('user_name', callers[u])
			.orderByDesc('inc_sys_created_on')
			.select('inc_number', 'user_name', 'inc_sys_created_on')
			.limit(5)
			.forEach(template.print(function (recrd){
			var res = ' Incident Number: '+recrd.inc_number+' was raised by user named:                                               '+recrd.user_name+' on: '+recrd.inc_sys_created_on;
				return res;
			}));
			template.print('<br>---------------------------------------------------------------------------------------------------------------------------------------------------------');
			temp=callers[u];
		}

})(current, template, email, email_action, event);
