sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) 
		  {
			  var usersList= new GlideRecord('sys_user');
			  usersList.addQuery('active', true);
			  usersList.orderBy('name');
			  usersList.query();
			  var inc_total = 0;
			  while(usersList.next())
			  {
				  //template.print('<br><h3>********** '+usersList.name+' **********</h3><br>');				  
			  	  var incidentList= new GlideRecord('incident');
				  incidentList.addQuery('caller_id', usersList.getUniqueValue());
				  incidentList.addQuery('active', true);
				  incidentList.orderByDesc('sys_created_on');
				  incidentList.setLimit(5);
				  incidentList.query();
				  var c =0;
				  while(incidentList.next())
				  {
					  c+=1;
					  inc_total+=1;
					  var res = '('+c+')'+' Incident Number: '+incidentList.getValue('number')+' was raised by the user named: '+usersList.name+' on: '+incidentList.sys_created_on+'<br><br>'; 
					  template.print(res);
				  }
				  if(c>0)
				  {
					  template.print('<br><br>');
				  }
				//   if(c==0)
				//   {
				// 	  template.print('No Incident has been raised by this user so far....');
				//   }				 
			  }
				 template.print('<br><br>'+inc_total);

		  })(current, template, email, email_action, event);
