sys_script_include.script:
var CheckForNumberOfReportees = Class.create();
CheckForNumberOfReportees.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkReportees:function()
	{
		var users_list = new GlideRecord('sys_user');
		//users_list.addEncodedQuery('roles=x_58872_needit.reporting_manager');
		//users_list.addQuery('sys_id', this.getParameter('sysparm_caller_id'));
		users_list.addQuery('manager', this.getParameter('sysparm_manager'));
		users_list.query();
		var reportees = users_list.getRowCount();
		if(reportees>4)
			{
				return false;
			}
		return true;
	},
    type: 'CheckForNumberOfReportees'
});
