sys_script_include.script:
var GetLastFiveIncidentsOfAUser = Class.create();
GetLastFiveIncidentsOfAUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor, 
{
	getIncidents:function()
	{
		var incArr = [];
		var currInc;
		var incidentsList = new GlideRecord('incident');
		incidentsList.addQuery('caller_id', this.getParameter('sysparm_caller_id'));
		incidentsList.orderByDesc('caller_id');
		incidentsList.setLimit(5);
		incidentsList.query();
		while(incidentsList.next())
		{
			currInc = incidentsList.number;
			incArr.push(currInc+'');
		}
		return JSON.stringify(incArr.join(', '));
	},
    type: 'GetLastFiveIncidentsOfAUser'
});
