sysauto_script.script:
//First get current time
var rightNow = new GlideDateTime();

//Establish the connection to database and related table using GlideRecord object.
var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');

//we use a GlideTime object to set the time to 24 hours and then add this time to the rightNow object to create a new GlideDateTime object with time as 24 hours ahead of now.
//var endTime=rightNow.addDaysLocalTime(1);

 //after this we apply filter conditions to retrieve the specific records
dueSoonNITasks.addQuery('due_date','>=',rightNow);
dueSoonNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
dueSoonNITasks.addQuery('state','!=',3);

//Now we query the database
dueSoonNITasks.query();

// Write a log message for each due soon NeedIt Task Record
while(dueSoonNITasks.next())
{
	gs.info("NeedIt Task records that are due soon are = " + dueSoonNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoonNITasks,dueSoonNITasks.number,gs.getUserName());
}
