sysauto_script.script:
var usersWithInc=[];
var inc_list=new GlideRecord('incident');
inc_list.addQuery('active', true);
inc_list.query();
while(inc_list.next())
{
	var userN = inc_list.getValue('caller_id'); 
	usersWithInc.push(userN);
}

//removing duplicate user names from usersWithInc array.

function removeDuplicateUsers(usersArr) 
{
	var uniqueUsers=[];
	uniqueUsers[0]=usersArr[0];
	for(var i=0; i<usersArr.length; i++)
		{
			var u = usersArr[i];			
			for(var j=0; j<=uniqueUsers.length;j++)
				{
					if(!uniqueUsers[j]===u)
					{
						uniqueUsers.push(u);
					}
				}			
		}
    return uniqueUsers;
}
//Lastly, fire an event which will cause the notification to be sent.
var uniqueU = removeDuplicateUsers(usersWithInc);
gs.eventQueue('x_58872_needit.latestIncidents', inc_list, usersWithInc, inc_list);
